        optional.orElse("");
        //引用缺失时传入一个函数返回特定的值
        optional.orElseGet(
                () -> {
                    return "自定义返回的值"
                }
        );
        //引用缺失的时候抛出一个异常
        optional.orElseThrow(() -> {
            throw new RuntimeException("异常");
        });

    }

